import React, { useState, useEffect } from "react";

export default function Header(
  { onSelectedTopicChange },
  { onSelectedQuestionChange }
) {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [searchTopic, setSearchTopic] = useState("");

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentDate(new Date());
    }, 1000); // Update the date and time every second
    return () => clearInterval(timer); // Clear the interval on component unmount
  }, []);

   const handleInputChange = (event) => {
     setSearchTopic(event.target.value);
   };

  const handleSubmit = (event) => {
    event.preventDefault(); // Prevent the default form submit action
    onSelectedQuestionChange(`&q=${searchTopic}`);
  };

  return (
    <div id="header">
      <p id="dateAndTime">{currentDate.toLocaleString()}</p>
      <button onClick={() => onSelectedTopicChange("&category=general")}>
        General
      </button>
      <button onClick={() => onSelectedTopicChange("&category=business")}>
        Business
      </button>
      <button onClick={() => onSelectedTopicChange("&category=science")}>
        Science
      </button>
      <button onClick={() => onSelectedTopicChange("&category=health")}>
        Health
      </button>
      <button onClick={() => onSelectedTopicChange("&category=technology")}>
        Technology
      </button>
      <button onClick={() => onSelectedTopicChange("&category=sports")}>
        Sports
      </button>
      <button onClick={() => onSelectedTopicChange("&category=entertainment")}>
        Entertainment
      </button>
      <form id="searchForm" onSubmit={handleSubmit}>
        <input
          type="text"
          defaultValue="search for a topic"
          onChange={handleInputChange}
        ></input>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}
