import React, { useState, useEffect } from "react";

export default function Overview() {
  const [articles, setArticles] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    fetch(
      "https://newsapi.org/v2/top-headlines?country=us&apiKey=a79bae5861af40bc998c65b5fc741a6b"
    )
      .then((res) => res.json())
      .then((data) => {
        setArticles(data.articles); // Set the articles
        setIsLoading(false); // Set loading to false after fetching data
      })
      .catch((error) => {
        console.error("Error fetching data: ", error);
        setIsLoading(false); // Also set loading to false in case of error
      });
  }, []);

  if (isLoading) {
    return <div>Loading...</div>;
  }


  return (
    <div>
      <div className="container">
        <div class="row">
          {articles.map((article, index) => (
            <div key={index}>
              <div class="col-sm-3 col-md-6">
                <img src={article.urlToImage} alt={article.title}></img>
                <h2>{article.title}</h2> {/* Corrected access to title */}
                <p>{article.author}</p> {/* Corrected access to author */}
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
