import React, { useState, useEffect } from "react";
import Search from "./Search";

export default function Header(onSelectedRegionChange) {
  const [currentDate, setCurrentDate] = useState(new Date());

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentDate(new Date());
    }, 1000); // Update the date and time every second
    return () => clearInterval(timer); // Clear the interval on component unmount
  }, []);

  return (
    <div style={{ display: "flex" }}>
      <p>
        <button onClick={() => onNewsTypeChange("at")}>
          Austrian News
        </button>
      </p>
      <p>
        <button onClick={() => onNewsTypeChange("US")}>
          US News
        </button>
      </p>
      <p id="dateAndTime">{currentDate.toLocaleString()}</p>
      <Search />
    </div>
  );
}
