        { selectedRegion } +
        "apiKey=a79bae5861af40bc998c65b5fc741a6b"
    )
      .then((res) => res.json())
      .then((data) => {
        setArticles(data.articles);
        setIsLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching data: ", error);
