import React, { useState, useEffect } from "react";

export default function Header({
  onSelectedTopicChange,
  onSelectedQuestionChange,
}) {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [searchTopic, setSearchTopic] = useState("");

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentDate(new Date());
    }, 1000); // Update the date and time every second
    return () => clearInterval(timer); // Clear the interval on component unmount
  }, []);

  const handleInputChange = (event) => {
    setSearchTopic(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault(); // Prevent the default form submit action
    onSelectedQuestionChange(`&q=${searchTopic}`);
  };

  return (
    <div id="header">
        <nav class="navbar navbar-expand-lg navbar-light bg-light">
  <a class="navbar-brand" href="#">Navbar</a>
  <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
    <span class="navbar-toggler-icon"></span>
  </button>
  <div class="collapse navbar-collapse" id="navbarSupportedContent">
    <ul class="navbar-nav mr-auto">
      <li class="nav-item active">
        <button id="dateAndTime" disabled>
        {currentDate.toLocaleString()}
      </button>
      </li>
      <li class="nav-item">
          <button onClick={() => onSelectedTopicChange("&category=general")}>
        General
      </button>
      </li>
      <li class="nav-item">
         <button onClick={() => onSelectedTopicChange("&category=business")}>
        Business
      </button>
      </li>
      <li class="nav-item">
          <button onClick={() => onSelectedTopicChange("&category=science")}>
        Science
      </button>
      </li>
      <li class="nav-item">
          <button onClick={() => onSelectedTopicChange("&category=health")}>
        Health
      </button>
      </li>
      <li class="nav-item">
         <button onClick={() => onSelectedTopicChange("&category=technology")}>
        Technology
      </button>
      </li>
      <li class="nav-item">
         <button onClick={() => onSelectedTopicChange("&category=sports")}>
        Sports
      </button>
      </li>
      <li class="nav-item">
         <button onClick={() => onSelectedTopicChange("&category=entertainment")}>
        Entertainment
      </button>
      </li>
    </ul>
    <form class="form-inline my-2 my-lg-0">
      <input class="form-control mr-sm-2" type="search" placeholder="Search" aria-label="Search"></input>
      <button class="btn btn-outline-success my-2 my-sm-0" type="submit">Search</button>
    </form>
  </div>
</nav>








      <form id="searchForm" onSubmit={handleSubmit}>
        <input type="text" onChange={handleInputChange}></input>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}
