import React, { useState, useEffect } from "react";
import {
  Navbar,
  Nav,
  NavItem,
  Form,
  FormControl,
  Button,
} from "react-bootstrap";

export default function Header({
  onSelectedTopicChange,
  onSelectedQuestionChange,
}) {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [searchTopic, setSearchTopic] = useState("");

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentDate(new Date());
    }, 1000);
    return () => clearInterval(timer);
  }, []);

  const handleInputChange = (event) => {
    setSearchTopic(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    onSelectedQuestionChange(`&q=${searchTopic}`);
  };

  return (
    <Navbar bg="light" expand="lg">
      <Navbar.Brand href="#">Daily News</Navbar.Brand>
      <Navbar.Toggle aria-controls="basic-navbar-nav" />
      <Navbar.Collapse id="basic-navbar-nav">
        <Nav className="mr-auto">
          <NavItem>
            <Button variant="light" disabled className="Button">
              {currentDate.toLocaleString()}
            </Button>
          </NavItem>
          <NavItem>
            <Button
              variant="light"
              onClick={() => onSelectedTopicChange("&category=general")}
            >
              General
            </Button>
          </NavItem>
          <NavItem>
            <Button
              variant="light"
              className="Button"
              onClick={() => onSelectedTopicChange("&category=business")}
            >
              Business
            </Button>
          </NavItem>
          <NavItem>
            <Button
              variant="light"
              onClick={() => onSelectedTopicChange("&category=science")}
            >
              Science
            </Button>
          </NavItem>
          <NavItem>
            <Button
              variant="light"
              onClick={() => onSelectedTopicChange("&category=health")}
            >
              Health
            </Button>
          </NavItem>
          <NavItem>
            <Button
              variant="light"
              onClick={() => onSelectedTopicChange("&category=technology")}
            >
              Technology
            </Button>
          </NavItem>
          <NavItem>
            <Button
              variant="light"
              onClick={() => onSelectedTopicChange("&category=sports")}
            >
              Sports
            </Button>
          </NavItem>
          <NavItem>
            <Button
              variant="light"
              onClick={() => onSelectedTopicChange("&category=entertainment")}
            >
              Entertainment
            </Button>
          </NavItem>
        </Nav>
        <Form inline="true" onSubmit={handleSubmit} className="form">
          <FormControl
            type="search"
            placeholder="Search"
            className="mr-sm-2 form"
            onChange={handleInputChange}
            id="searchInput"
            name="search"
          />
          <Button variant="outline-success" type="submit">
            Search
          </Button>
        </Form>
      </Navbar.Collapse>
    </Navbar>
  );
}
