import React, { useState, useEffect } from "react";

export default function Header(onSelectedRegionChange) {
  const [currentDate, setCurrentDate] = useState(new Date());

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentDate(new Date());
    }, 1000); // Update the date and time every second
    return () => clearInterval(timer); // Clear the interval on component unmount
  }, []);

  return (
    <div id="header">
      <p id="dateAndTime">{currentDate.toLocaleString()}</p>
      <button onClick={() => onSelectedRegionChange("?country=at")}>
        Austrian News
      </button>
      <button onClick={() => onSelectedRegionChange("?country=us")}>
        US News
      </button>
      <form id="searchForm">
        <input type="text" defaultValue="search for a topic"></input>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}
