import React, { useState, useEffect } from "react";
import { Navbar, Nav, NavForm, FormControl, Button } from "react-bootstrap";

export default function Header({
  onSelectedTopicChange,
  onSelectedQuestionChange,
}) {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [searchTopic, setSearchTopic] = useState("");

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentDate(new Date());
    }, 1000);
    return () => clearInterval(timer);
  }, []);

  const handleInputChange = (event) => {
    setSearchTopic(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    onSelectedQuestionChange(`&q=${searchTopic}`);
  };

  return (
    <Navbar bg="light" expand="lg">
      <Navbar.Brand href="#">Navbar</Navbar.Brand>
      <Navbar.Toggle aria-controls="basic-navbar-nav" />
      <Navbar.Collapse id="basic-navbar-nav">
        <Nav className="mr-auto">
          <NavItem>
            <Button variant="light" disabled>
              {currentDate.toLocaleString()}
            </Button>
          </NavItem>
          <NavItem>
            <Button onClick={() => onSelectedTopicChange("&category=general")}>
              General
            </Button>
          </NavItem>
          <NavItem>
            <Button onClick={() => onSelectedTopicChange("&category=business")}>
              Business
            </Button>
          </NavItem>
          <NavItem>
            <Button onClick={() => onSelectedTopicChange("&category=science")}>
              Science
            </Button>
          </NavItem>
          <NavItem>
            <Button onClick={() => onSelectedTopicChange("&category=health")}>
              Health
            </Button>
          </NavItem>
          <NavItem>
            <Button
              onClick={() => onSelectedTopicChange("&category=technology")}
            >
              Technology
            </Button>
          </NavItem>
          <NavItem>
            <Button onClick={() => onSelectedTopicChange("&category=sports")}>
              Sports
            </Button>
          </NavItem>
          <NavItem>
            <Button
              onClick={() => onSelectedTopicChange("&category=entertainment")}
            >
              Entertainment
            </Button>
          </NavItem>
        </Nav>
        <Form inline onSubmit={handleSubmit}>
          <FormControl
            type="search"
            placeholder="Search"
            className="mr-sm-2"
            onChange={handleInputChange}
          />
          <Button variant="outline-success" type="submit">
            Search
          </Button>
        </Form>
      </Navbar.Collapse>
    </Navbar>
  );
}
