import React, { useState, useEffect } from "react";

export default function Overview() {
  const [articles, setArticles] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    fetch(
      "https://newsapi.org/v2/top-headlines?country=us&apiKey=a79bae5861af40bc998c65b5fc741a6b"
    )
      .then((res) => res.json())
      .then((data) => {
        setArticles(data.articles); // Set the articles
        setIsLoading(false); // Set loading to false after fetching data
      })
      .catch((error) => {
        console.error("Error fetching data: ", error);
        setIsLoading(false); // Also set loading to false in case of error
      });
  }, []);

  if (isLoading) {
    return <div>Loading...</div>;
  }

  let counter = 1;

  return (
    <div className="container">
      <div className="row">
        {articles.map((article, index) => ({
          if() {
            <div key={index} className="col-md-4 mb-3">
              <div className="card">
                <img
                  src={article.urlToImage}
                  alt={article.title}
                  className="card-img-top"
                />
                <div className="card-body">
                  <h2 className="card-title">{article.title}</h2>
                  <p className="card-text">{article.author}</p>
                </div>
              </div>
            </div>;
          },
        }))}
      </div>
    </div>
  );
}
